func (mvgb *MessageVoiceGroupBy) sqlScan(ctx context.Context, root *MessageVoiceQuery, v any) error {
	selector := root.sqlQuery(ctx).Select()
	aggregation := make([]string, 0, len(mvgb.fns))
	for _, fn := range mvgb.fns {
		aggregation = append(aggregation, fn(selector))
	}
	if len(selector.SelectedColumns()) == 0 {
		columns := make([]string, 0, len(*mvgb.flds)+len(mvgb.fns))
		for _, f := range *mvgb.flds {
			columns = append(columns, selector.C(f))
		}
		columns = append(columns, aggregation...)
		selector.Select(columns...)
	}
	selector.GroupBy(selector.Columns(*mvgb.flds...)...)
	if err := selector.Err(); err != nil {
		return err
	}
	rows := &sql.Rows{}
	query, args := selector.Query()
	if err := mvgb.build.driver.Query(ctx, query, args, rows); err != nil {
		return err
	}
	defer rows.Close()
	return sql.ScanSlice(rows, v)
}

// MessageVoiceSelect is the builder for selecting fields of MessageVoice entities.
type MessageVoiceSelect struct {
	*MessageVoiceQuery
	selector
}

// Aggregate adds the given aggregation functions to the selector query.
func (mvs *MessageVoiceSelect) Aggregate(fns ...AggregateFunc) *MessageVoiceSelect {
	mvs.fns = append(mvs.fns, fns...)
	return mvs
}

// Scan applies the selector query and scans the result into the given value.
func (mvs *MessageVoiceSelect) Scan(ctx context.Context, v any) error {
	ctx = setContextOp(ctx, mvs.ctx, ent.OpQuerySelect)
	if err := mvs.prepareQuery(ctx); err != nil {
		return err
	}
	return scanWithInterceptors[*MessageVoiceQuery, *MessageVoiceSelect](ctx, mvs.MessageVoiceQuery, mvs, mvs.inters, v)
}

func (mvs *MessageVoiceSelect) sqlScan(ctx context.Context, root *MessageVoiceQuery, v any) error {
	selector := root.sqlQuery(ctx)
	aggregation := make([]string, 0, len(mvs.fns))
	for _, fn := range mvs.fns {
		aggregation = append(aggregation, fn(selector))
	}
	switch n := len(*mvs.selector.flds); {
	case n == 0 && len(aggregation) > 0:
		selector.Select(aggregation...)
	case n != 0 && len(aggregation) > 0:
		selector.AppendSelect(aggregation...)
	}
	rows := &sql.Rows{}
	query, args := selector.Query()
	if err := mvs.driver.Query(ctx, query, args, rows); err != nil {
		return err
	}
	defer rows.Close()
	return sql.ScanSlice(rows, v)
}
